import React, { useEffect } from "react";
import styled, { keyframes } from "styled-components";

// ==== Animations ====
const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(40px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

// ==== Styled Components ====
const Section = styled.section`
  padding: 5rem 2rem;
  background: #f7fbff;
  text-align: center;
`;

const Title = styled.h2`
  font-size: 2.2rem;
  font-weight: 700;
  color: #111;
  margin-bottom: 3rem;
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 2.5rem;
  justify-items: center;
  margin-bottom: 4rem;
`;

const Card = styled.div`
  background: #fff;
  border-radius: 20px;
  box-shadow: 0 8px 30px rgba(0, 0, 0, 0.07);
  padding: 2.5rem 2rem;
  max-width: 340px;
  text-align: center;
  transition: all 0.3s ease;
  opacity: 0;
  transform: translateY(40px);

  &.visible {
    animation: ${fadeInUp} 0.6s ease forwards;
  }

  &:hover {
    transform: translateY(-8px);
    box-shadow: 0 12px 30px rgba(0, 0, 0, 0.1);
  }
`;

const Image = styled.img`
  width: 110px;
  height: 110px;
  border-radius: 50%;
  object-fit: cover;
  margin-bottom: 1rem;
`;

const Name = styled.h3`
  font-size: 1.2rem;
  font-weight: 600;
  color: #222;
  margin-bottom: 0.3rem;
`;

const Role = styled.p`
  color: #777;
  font-size: 0.95rem;
  margin-bottom: 1.2rem;
`;

const LogoRow = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 0.8rem;
  flex-wrap: wrap;
  margin-bottom: 1rem;

  img {
    width: 28px;
    height: 28px;
    object-fit: contain;
  }
`;

const Teaches = styled.div`
  font-size: 0.9rem;
  color: #555;
  margin-bottom: 1.5rem;
  p:first-child {
    font-weight: 600;
    color: #777;
    margin-bottom: 0.3rem;
  }
`;

const LinkedIn = styled.a`
  display: inline-block;
  color: #4460d4;
  font-weight: 600;
  font-size: 0.95rem;
  text-decoration: none;
  transition: 0.3s;
  &:hover {
    color: #2e47b8;
    text-decoration: underline;
  }
`;

const ButtonsContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  flex-wrap: wrap;
`;

const Button = styled.button`
  border: none;
  border-radius: 8px;
  font-weight: 600;
  font-size: 1rem;
  padding: 0.9rem 2rem;
  cursor: pointer;
  transition: 0.3s ease;
  ${({ primary }) =>
    primary
      ? `
    background: #ff5722;
    color: #fff;
    &:hover {
      background: #e14a17;
    }
  `
      : `
    background: #111;
    color: #fff;
    &:hover {
      background: #333;
    }
  `}
`;

// ==== Component ====
const Faculty = () => {
  const instructors = [
    {
      name: "Ankush Singla",
      role: "Co-Founder & Instructor",
      img: "data:image/jpeg;base64,",
      logos: [
        "data:image/jpeg;base64,",
        "https://upload.wikimedia.org/wikipedia/commons/a/a9/Amazon_logo.svg",
        "https://upload.wikimedia.org/wikipedia/commons/0/05/Facebook_Logo_%282019%29.png",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSCHxwIljd6IcFK0LI1ixqXUVi3jxTWqYIQ-3S2qJ42kw&s",
      ],
      teaches: ["Data Structures & Algorithms", "Competitive Programming"],
      linkedin: "#",
    },
    {
      name: "Nidhi Aggarwal",
      role: "Instructor",
      img: "data:image/jpeg;base64,",
      logos: [
        "data:image/jpeg;base64,",
      ],
      teaches: ["Basics of Java with DSA"],
      linkedin: "#",
    },
    {
      name: "Manisha Khattar",
      role: "Instructor",
      img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQl7CKQnyLCn4pOgTEqotc_EsSXtNcnJyCG8Q&s",
      logos: [
        "https://upload.wikimedia.org/wikipedia/commons/2/2f/Google_2015_logo.svg",
        "data:image/jpeg;base64,",
      ],
      teaches: ["MERN Stack"],
      linkedin: "#",
    },
  ];

  // Fade in cards when scrolled into view
  useEffect(() => {
    const cards = document.querySelectorAll(".faculty-card");
    const handleScroll = () => {
      cards.forEach((card) => {
        const rect = card.getBoundingClientRect();
        if (rect.top < window.innerHeight - 100) {
          card.classList.add("visible");
        }
      });
    };
    window.addEventListener("scroll", handleScroll);
    handleScroll();
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <Section>
      <Title>Faculty that brings out the best in you</Title>
      <Grid>
        {instructors.map((inst, i) => (
          <Card key={i} className="faculty-card">
            <Image src={inst.img} alt={inst.name} />
            <Name>{inst.name}</Name>
            <Role>{inst.role}</Role>
            <LogoRow>
              {inst.logos.map((logo, index) => (
                <img src={logo} alt="affiliation logo" key={index} />
              ))}
            </LogoRow>
            <Teaches>
              <p>Teaches</p>
              {inst.teaches.map((t, index) => (
                <div key={index}>{t}</div>
              ))}
            </Teaches>
            <LinkedIn href={inst.linkedin} target="_blank">
              LinkedIn profile 
            </LinkedIn>
          </Card>
        ))}
      </Grid>

      <ButtonsContainer>
        <Button>Request callback</Button>
        <Button primary>Book a free webinar</Button>
      </ButtonsContainer>
    </Section>
  );
};

export default Faculty;








